import React from 'react';
import { View, ScrollView, Image, FlatList, TouchableOpacity, StyleSheet } from 'react-native';
import { Card, Title } from 'react-native-paper';
import HTML from 'react-native-render-html';
import { IGNORED_TAGS } from 'react-native-render-html/src/HTMLUtils';
import Axios from 'axios';

const Post = ({route, navigation}) => {
    const [post, setPost] = React.useState([]);

    const {id} = route.params;
    
    React.useEffect(() => {
        Axios.get(`http://arthurmurraydcp.com/wp-json/wp/v2/posts?&include=${id}`).then(response => {
            setPost(response.data);
        });
    }, [setPost]);

    return (
        <View style = {styles.container}>
            <FlatList
                data = {screen == 'Home' ? dancePosts : musicPosts}
                keyExtractor = {item => item.id.toString()}
                ListHeaderComponent = {
                    <Image
                        source = { require('../../assets/logo.png') }
                        style = {{ resizeMode: "contain", width: 420, height: 90}}
                    />
                }
                renderItem = {({item}) => (
                    <TouchableOpacity onPress = {() => {navigation.navigate("Post", {id: item.id})}} activeOpacity = {0.9}>
                        <Card style = {styles.card}>
                            <Card.Content>
                                <Title style = {styles.title}> <HTML source={{html: item.title.rendered}}/></Title>
                            </Card.Content>
                            <Card.Cover source = {{ uri: item.featured_media_src_url }}/>
                        </Card>
                    </TouchableOpacity>
                )}
            />
        </View>
    );
    
