import React from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import HTML from 'react-native-render-html';
import Axios from 'axios';
import { StyleSheet, Dimensions, Image, FlatList, Text, View, ScrollView, TouchableOpacity } from 'react-native';
import { Avatar, Button, Card, Title, Paragraph, List } from 'react-native-paper';
import { IGNORED_TAGS } from 'react-native-render-html/src/HTMLUtils';
import { WebView } from 'react-native-webview';

const Post = ({navigation}) => {
    const [post, setPost] = React.useState([]);
    const post_id = AsyncStorage.getItem('post_id');
    console.log(post_id);
    React.useEffect(() => {
        Axios.get(`http://arthurmurraydcp.com/wp-json/wp/v2/posts?&include=${post_id}`).then(response => {
            setPost(response.data); console.log(response.data);
        });
    }, [setPost]);

    return (
        <ScrollView style = {{flex:1, backgroundColor: 'rgba(231, 207, 200, 0.9)'}}>
            <HTML
                    ignoredTags={IGNORED_TAGS.filter(tag => tag !== 'video')}
                    // source = {{uri: post.content.rendered}}
                    renderers={{
                        video: (htmlAttribs, children, convertedCSSStyles, passProps) => {
                            return <View key={passProps.key} style={{ width: "100%", aspectRatio:16.0/9.0, marginTop:16, marginBottom:16 }}>
                                <WebView
                                    scrollEnabled={false}
                                    mediaPlaybackRequiresUserAction={true}
                                    allowsInlineMediaPlayback={true}
                                    javaScriptEnabled={true}
                                    scalesPageToFit={false}
                                    allowsFullscreenVideo={true}
                                    domStorageEnabled={true}
                                    allowFileAccess={false}
                                    source = {{ html: `
                                    <video width="100%" height="100%" controls>
                                        <source src="${htmlAttribs.src}" type="video/mp4">
                                    </video>
                                    ` }}
                                    style={{ flex: 1, width: "100%", aspectRatio: 16.0 / 9.0 }}
                                />
                            </View>
                        },
                        iframe: (htmlAttribs, children, convertedCSSStyles, passProps) => {
                            return <View key={passProps.key} style={{ width: "100%", aspectRatio:16.0/9.0, marginTop:16, marginBottom:16 }}>
                                <WebView
                                    scrollEnabled={false}
                                    mediaPlaybackRequiresUserAction={true}
                                    allowsInlineMediaPlayback={true}
                                    javaScriptEnabled={true}
                                    injectedJavaScript={INJECTEDJAVASCRIPT}
                                    onMessage={() => {}}
                                    scalesPageToFit={false}
                                    allowsFullscreenVideo={true}
                                    domStorageEnabled={true}
                                    allowFileAccess={false}
                                    source={{ uri: htmlAttribs.src }}
                                    style={{ flex: 1, width: "100%", aspectRatio: 16.0 / 9.0 }}
                                />
                            </View> 
                        }
                    }}
                />
            </ScrollView>
        // <View style = {styles.container}>
        //     <FlatList
        //         data = {screen == 'Home' ? dancePosts : musicPosts}
        //         keyExtractor = {item => item.id.toString()}
        //         ListHeaderComponent = {
        //             <Image
        //                 source = { require('../../assets/logo.png') }
        //                 style = {{ resizeMode: "contain", width: 420, height: 90}}
        //             />
        //         }
        //         renderItem = {({item}) => (
        //             <TouchableOpacity onPress = {() => {navigation.navigate("Post", {post_id: item.id})}} activeOpacity = {0.9}>
        //                 <Card style = {styles.card}>
        //                     <Card.Content>
        //                         <Title style = {styles.title}> <HTML source={{html: item.title.rendered}}/></Title>
        //                     </Card.Content>
        //                     <Card.Cover source = {{ uri: item.featured_media_src_url }}/>
        //                 </Card>
        //             </TouchableOpacity>
        //         )}
        //     />
        // </View>
    );
}

export default Post;

const styles = StyleSheet.create({
    container: {
        flex: 1
    },
    card: {
        width: '90%',
        alignSelf: 'center',
        backgroundColor: '#FFFFFF',
        marginVertical: 10
    },
    title: {
        textAlign: 'center'
    },
    image: {
        width: 420, height: 90,
        resizeMode: "contain"
    }
});

