import React from 'react';
import { View, ScrollView, Image, FlatList, TouchableOpacity, StyleSheet } from 'react-native';
import { Card, Title } from 'react-native-paper';
import HTML from 'react-native-render-html';
import { IGNORED_TAGS } from 'react-native-render-html/src/HTMLUtils';
import Axios from 'axios';

const Post = ({route, navigation}) => {
    const [post, setPost] = React.useState([]);
    
    React.useEffect(() => {
        const post_id = route.params.post_id;
        console.log(post_id);
    }, [route.params?.post_id]);
    
    
    React.useEffect(() => {
        Axios.get(`http://arthurmurraydcp.com/wp-json/wp/v2/posts?&include=${post_id}`).then(response => {
            setPost(response.data);
        });
    }, [setPost]);

    return (
        <ScrollView style = {{flex:1, backgroundColor: 'rgba(231, 207, 200, 0.9)'}}>
            <HTML
                ignoredTags={IGNORED_TAGS.filter(tag => tag !== 'video')}
                source = {{uri: post.content}}
                renderers={{
                    video: (htmlAttribs, children, convertedCSSStyles, passProps) => {
                        return <View key={passProps.key} style={{ width: "100%", aspectRatio:16.0/9.0, marginTop:16, marginBottom:16 }}>
                            <WebView
                                scrollEnabled={false}
                                mediaPlaybackRequiresUserAction={true}
                                allowsInlineMediaPlayback={true}
                                javaScriptEnabled={true}
                                scalesPageToFit={false}
                                allowsFullscreenVideo={true}
                                domStorageEnabled={true}
                                allowFileAccess={false}
                                source = {{ html: `
                                <video width="100%" height="100%" controls>
                                    <source src="${htmlAttribs.src}" type="video/mp4">
                                </video>
                                ` }}
                                style={{ flex: 1, width: "100%", aspectRatio: 16.0 / 9.0 }}
                            />
                        </View>
                    },
                    iframe: (htmlAttribs, children, convertedCSSStyles, passProps) => {
                        return <View key={passProps.key} style={{ width: "100%", aspectRatio:16.0/9.0, marginTop:16, marginBottom:16 }}>
                            <WebView
                                scrollEnabled={false}
                                mediaPlaybackRequiresUserAction={true}
                                allowsInlineMediaPlayback={true}
                                javaScriptEnabled={true}
                                injectedJavaScript={INJECTEDJAVASCRIPT}
                                onMessage={() => {}}
                                scalesPageToFit={false}
                                allowsFullscreenVideo={true}
                                domStorageEnabled={true}
                                allowFileAccess={false}
                                source={{ uri: htmlAttribs.src }}
                                style={{ flex: 1, width: "100%", aspectRatio: 16.0 / 9.0 }}
                            />
                        </View> 
                    }
                }}
            />
        </ScrollView>
    );
}

export default Post;

const styles = StyleSheet.create({
    container: {
        flex: 1
    },
    card: {
        width: '90%',
        alignSelf: 'center',
        backgroundColor: '#FFFFFF',
        marginVertical: 10
    },
    title: {
        textAlign: 'center'
    },
    image: {
        width: 420, height: 90,
        resizeMode: "contain"
    }
});

